import {useState} from "react";
import "./App.css";

function App() {
  const [count,setCount] = useState(0);

  function decreaseHandler(){
    setCount = count-1;
  }
  function increaseHandler(){
    setCount = count+1;
  }
  function resetHandler(){
    setCount(0);
  }

  return (
   <div className="w-[100vw] h-[100vh] text-3xl flex justify-center items-center bg-[#344151] flex-col gap-10">
    <div className="text-[#0398d4] font-medium ">Increment & Decrement</div>
    <div className="bg-white flex justify-center gap-12 py-3 rounded-sm text-[25px] text-[#344151]">
      <button onClick={decreaseHandler} className="border-r-2 text-center w-20 border">
        -
      </button>
      <div>

      </div>
      <button onClick={increaseHandler}>
        +
      </button>
    </div>
    <button onClick={resetHandler}>
      Reset
    </button>
   </div>
  );
}

export default App;
